# generates an R function that we can pass to a tuner to optrimize hyper parstunerFitnFun = function(xs, learner, task, resampling, measures, par.set, ctrl,   opt.path, show.info, log.fun, trafo, convertx) {    xs = convertx(xs)  #FIXME: what happens if we leave constraints? svm with nelder mead e.g.  # transform parameters  xs = if (trafo)     lapply(xs, trafoValue, par=par.set)  else     xs  dob = ifelse(getOptPathLength(opt.path) == 0, 1, max(opt.path$env$dob) + 1)  ys = evalOptimizationStates(learner, task, resampling, measures, par.set, NULL, ctrl,     opt.path, show.info, log.fun, xs, dobs=dob, eols=NA)   #returns list of resample$aggr vectors, take 1st    ys = sapply(ys, function(a) a[1])  ifelse(measures[[1]]$minimize, 1 , -1) * ys}  