library(roxygen)library(mlr)source("src/mlrEnsemble/BaseCombiner.R")source("src/mlrEnsemble/SPOReinforcementEnsemble.R")library(mlbench)data(BostonHousing)mytask = makeRegrTask(data=BostonHousing, target="medv")w1 = makeLearner("regr.lm")w2 = makeLearner("regr.rpart")w3 = makeLearner("regr.earth")learners = list(w1, w2, w3)ctrl = list(alpha=1, beta=2)w.re = makeSPOReinforcementEnsemble(id="bla", learners, ctrl)m = train(w.re, mytask)p = predict(m, task=mytask)