test.holdout.instance = function() {  rin = makeResampleInstance(makeResampleDesc("Holdout", split=0.25), size=20)	checkEquals(rin@size, 20)	checkEquals(rin@desc@iters, 1)	checkEquals(length(rin@train.inds), 1)	checkEquals(length(rin@test.inds), 1)	checkEquals(length(rin@train.inds[[1]]), 5)	checkEquals(length(rin@test.inds[[1]]), 15)    rin = makeFixedHoldoutInstance(1:5, 1:15, size=20)	checkEquals(rin@size, 20)	checkEquals(rin@desc@iters, 1)	checkEquals(length(rin@train.inds), 1)	checkEquals(length(rin@test.inds), 1)	checkEquals(length(rin@train.inds[[1]]), 5)	checkEquals(length(rin@test.inds[[1]]), 15)}