testConvertDates = function() {  data = data.frame(    x=as.Date(c("2011-01-01", "2011-01-02", "2011-01-03")),    y=as.Date(c("2011-01-01", "2011-01-02", "2011-01-03"))  )  data2 = convertDates(data)  checkEquals(dim(data2), c(3, 6))  checkTrue(setequal(colnames(data2),     c("x_days", "x_months", "x_weekdays",      "y_days", "y_months", "y_weekdays")))  checkTrue(is.numeric(data2$x_days))  checkTrue(is.factor(data2$x_weekdays))  checkTrue(is.factor(data2$x_months))  checkEquals(data2$x_days, as.numeric(0:2))  checkEquals(data2$x_months, factor(rep("January", 3)))    data2 = convertDates(data, weekdays=FALSE)  checkEquals(dim(data2), c(3, 4))  checkTrue(setequal(colnames(data2),     c("x_days", "x_months",      "y_days", "y_months")))        data2 = convertDates(data, weekdays=FALSE, months=FALSE,     start.date=c(x="2011-01-02", y="2011-01-03"))  checkEquals(data2$x_days, as.numeric(-1:1))  checkEquals(data2$y_days, as.numeric(-2:0))}