testVarfilter <- function() {  z = varfilter(task=binaryclass.task, method="chi.squared", threshold=-1)  checkEquals(length(z$vars), sum(binaryclass.task@desc@n.feat))  z = varfilter(task=binaryclass.task, method="chi.squared", threshold=1000000)  checkEquals(length(z$vars), 0)  z = varfilter(task=binaryclass.task, method="random.forest.importance", threshold=-1)  checkEquals(length(z$vars), sum(binaryclass.task@desc@n.feat))  z = varfilter(task=binaryclass.task, method="random.forest.importance", threshold=1000000)  checkEquals(length(z$vars), 0)}